import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Navbar from './Navbar';
import '../styles/auth.css';

const Signup = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: '',
    profileImage: null
  });

  // 입력 필드 변경 핸들러
  const handleChange = (e) => {
    if (e.target.name === 'profileImage') {
      setFormData({ ...formData, profileImage: e.target.files[0] });
    } else {
      setFormData({ ...formData, [e.target.name]: e.target.value });
    }
  };

  // 회원가입 요청
  const handleSubmit = async (e) => {
    e.preventDefault();

    const data = new FormData();
    data.append('memberDTO', JSON.stringify({
      username: formData.username,
      email: formData.email,
      password: formData.password
    }));
    if (formData.profileImage) {
      data.append('authImgFile', formData.profileImage);
    }

    const response = await fetch('http://localhost:8080/api/users/join', {
      method: 'POST',
      body: data
    });

    const result = await response.json();
    alert(result.message);
    if (result.message === '회원가입 성공!') navigate('/login');
  };

  return (
    <div>
      <Navbar />
      <section className="auth-container">
        <h1>회원가입</h1>
        <form onSubmit={handleSubmit}>
          <input type="text" name="username" placeholder="아이디" onChange={handleChange} />
          <input type="email" name="email" placeholder="이메일" onChange={handleChange} />
          <input type="password" name="password" placeholder="비밀번호" onChange={handleChange} />
          <input type="file" name="profileImage" onChange={handleChange} />
          <button type="submit">회원가입</button>
        </form>
      </section>
    </div>
  );
};

export default Signup;
