import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Navbar from './Navbar';
import '../styles/global.css';

const Signup = () => {
  const navigate = useNavigate();

  // ✅ 단계 구분: step 1 (기본 정보) → step 2 (추가 정보)
  const [step, setStep] = useState(1);

  // ✅ 입력 데이터 상태 관리
  const [formData, setFormData] = useState({
    username: '',
    password: '',
    name: '',
    nickname: '',
    birth: '',
    gender: 'M',
    address: '',
    phone: '',
    email: '',
    introduction: '',
    portfolio: '',
    schoolName: '',
    major: '',
    grade: '',
    entranceDate: '',
    graduationDate: '',
    degree: '',
    skillCategory: [],
    desiredJob: [],
    experiences: [],
    careers: [],
    awards: [],
    profileImage: null
  });

  // ✅ 입력 필드 변경 핸들러
  const handleChange = (e) => {
    if (e.target.name === 'profileImage') {
      setFormData({ ...formData, profileImage: e.target.files[0] });
    } else {
      setFormData({ ...formData, [e.target.name]: e.target.value });
    }
  };

  // ✅ "다음" 버튼 클릭 시 step 변경
  const handleNextStep = () => {
    setStep(2);
  };

  // ✅ 회원가입 요청 처리
  const handleSubmit = async (e) => {
    e.preventDefault();

    const data = new FormData();
    data.append(
      'memberDTO',
      JSON.stringify({
        username: formData.username,
        password: formData.password,
        name: formData.name,
        nickname: formData.nickname,
        birth: formData.birth,
        gender: formData.gender,
        address: formData.address,
        phone: formData.phone,
        email: formData.email,
        introduction: formData.introduction,
        portfolio: [formData.portfolio],
        schoolName: formData.schoolName,
        major: formData.major,
        grade: formData.grade,
        entranceDate: formData.entranceDate,
        graduationDate: formData.graduationDate,
        degree: formData.degree,
        skillCategory: formData.skillCategory,
        desiredJob: formData.desiredJob,
        experiences: formData.experiences,
        careers: formData.careers,
        awards: formData.awards
      })
    );

    if (formData.profileImage) {
      data.append('authImgFile', formData.profileImage);
    }

    const response = await fetch('https://sequence-php.agong.store/api/users/join', {
      method: 'POST',
      body: data
    });

    const result = await response.json();
    alert(result.message);

    if (result.message === '회원가입 성공!') {
      navigate('/signup-success');
    }
  };

  return (
    <div>
      <Navbar />

      <section className="signup-container">
        <h1>회원가입</h1>
        <h2>{step === 1 ? '1/2' : '2/2'}</h2>

        {step === 1 ? (
          // ✅ Step 1: 기본 정보 입력
          <form className="signup-form">
            <div className="form-group">
              <label>이름</label>
              <input type="text" name="name" onChange={handleChange} required />
            </div>

            <div className="form-group">
              <label>생년월일</label>
              <input type="date" name="birth" onChange={handleChange} required />
            </div>

            <div className="form-group">
              <label>성별</label>
              <select name="gender" onChange={handleChange}>
                <option value="M">남성</option>
                <option value="F">여성</option>
              </select>
            </div>

            <div className="form-group">
              <label>휴대전화 번호</label>
              <input type="text" name="phone" onChange={handleChange} required />
            </div>

            <div className="form-group">
              <label>이메일</label>
              <input type="email" name="email" onChange={handleChange} required />
            </div>

            <div className="form-group">
              <label>주소</label>
              <input type="text" name="address" onChange={handleChange} required />
            </div>

            <button type="button" className="auth-button" onClick={handleNextStep}>
              다음
            </button>
          </form>
        ) : (
          // ✅ Step 2: 추가 정보 입력
          <form className="signup-form" onSubmit={handleSubmit}>
            <div className="form-group">
              <label>프로필 사진</label>
              <input type="file" name="profileImage" onChange={handleChange} />
            </div>

            <div className="form-group">
              <label>닉네임</label>
              <input type="text" name="nickname" onChange={handleChange} required />
            </div>

            <div className="form-group">
              <label>학교명</label>
              <input type="text" name="schoolName" onChange={handleChange} required />
            </div>

            <div className="form-group">
              <label>전공</label>
              <input type="text" name="major" onChange={handleChange} required />
            </div>

            <div className="form-group">
              <label>학년</label>
              <input type="text" name="grade" onChange={handleChange} required />
            </div>

            <div className="form-group">
              <label>입학 날짜</label>
              <input type="date" name="entranceDate" onChange={handleChange} required />
            </div>

            <div className="form-group">
              <label>졸업 날짜</label>
              <input type="date" name="graduationDate" onChange={handleChange} required />
            </div>

            <div className="form-group">
              <label>기술 스킬</label>
              <input type="text" name="skillCategory" onChange={handleChange} required />
            </div>

            <button type="submit" className="signup-button">
              회원가입
            </button>
          </form>
        )}
      </section>
    </div>
  );
};

export default Signup;
