import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Navbar from './Navbar';
import BASE_URL from './config.js'; // ✅ API URL 가져오기
import './styles/global.css';

const Signup = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: '',
    profileImage: null
  });

  // 입력값 변경 핸들러
  const handleChange = (e) => {
    if (e.target.name === 'profileImage') {
      setFormData({ ...formData, profileImage: e.target.files[0] });
    } else {
      setFormData({ ...formData, [e.target.name]: e.target.value });
    }
  };

  // 회원가입 요청
  const handleSubmit = async (e) => {
    e.preventDefault();

    const data = new FormData();
    data.append('memberDTO', new Blob([JSON.stringify({  // ✅ JSON을 Blob으로 변환
      username: formData.username,
      email: formData.email,
      password: formData.password
    })], { type: 'application/json' }));

    if (formData.profileImage) {
      data.append('authImgFile', formData.profileImage);
    }

    try {
      const response = await fetch(`https://sequence.agong.store/api/users/join`, { // ✅ URL 수정
        method: 'POST',
        body: data
      });

      const result = await response.json();
      alert(result.message);
      if (response.ok) navigate('/login');
    } catch (error) {
      console.error("회원가입 오류:", error);
      alert("서버 오류 발생");
    }
  };

  return (
    <div>
      <Navbar />
      <section className="auth-container">
        <h1>회원가입</h1>
        <form onSubmit={handleSubmit}>
          <input type="text" name="username" placeholder="아이디" onChange={handleChange} required />
          <input type="email" name="email" placeholder="이메일" onChange={handleChange} required />
          <input type="password" name="password" placeholder="비밀번호" onChange={handleChange} required />
          <input type="file" name="profileImage" onChange={handleChange} />
          <button type="submit">회원가입</button>
        </form>
      </section>
    </div>
  );
};

export default Signup;
