import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Navbar from './Navbar';
import '../styles/global.css';

const Signup = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: '',
    name: '',
    nickname: '',
    birth: '',
    gender: '',
    address: '',
    phone: '',
    introduction: '',
    schoolName: '',
    major: '',
    grade: '',
    entranceDate: '',
    graduationDate: '',
    degree: '',
    skillCategory: [],
    desiredJob: [],
    profileImage: null
  });

  const handleChange = (e) => {
    if (e.target.name === 'profileImage') {
      setFormData({ ...formData, profileImage: e.target.files[0] });
    } else {
      setFormData({ ...formData, [e.target.name]: e.target.value });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const data = new FormData();
    data.append('memberDTO', JSON.stringify({
      username: formData.username,
      email: formData.email,
      password: formData.password,
      name: formData.name,
      nickname: formData.nickname,
      birth: formData.birth,
      gender: formData.gender,
      address: formData.address,
      phone: formData.phone,
      introduction: formData.introduction,
      schoolName: formData.schoolName,
      major: formData.major,
      grade: formData.grade,
      entranceDate: formData.entranceDate,
      graduationDate: formData.graduationDate,
      degree: formData.degree,
      skillCategory: formData.skillCategory,
      desiredJob: formData.desiredJob
    }));
    if (formData.profileImage) {
      data.append('authImgFile', formData.profileImage);
    }

    const response = await fetch('https://sequence.agong.store/api/users/join', {
      method: 'POST',
      body: data
    });

    const result = await response.json();
    alert(result.message);
    if (result.message === '회원가입 성공!') navigate('/login');
  };

  return (
    <div>
      <Navbar />
      <section className="auth-container">
        <h1>회원가입</h1>
        <p>회원 정보를 입력하고 가입하세요.</p>

        <div className="recovery-form">
          <div className="form-group">
            <label htmlFor="username">아이디</label>
            <input type="text" name="username" placeholder="아이디 입력" onChange={handleChange} />
          </div>
          <div className="form-group">
            <label htmlFor="email">이메일</label>
            <input type="email" name="email" placeholder="이메일 입력" onChange={handleChange} />
          </div>
          <div className="form-group">
            <label htmlFor="password">비밀번호</label>
            <input type="password" name="password" placeholder="비밀번호 입력" onChange={handleChange} />
          </div>
          <div className="form-group">
            <label htmlFor="profileImage">프로필 이미지</label>
            <input type="file" name="profileImage" onChange={handleChange} />
          </div>
        </div>

        <button className="recovery-button" onClick={handleSubmit}>회원가입</button>
      </section>
    </div>
  );
};

export default Signup;
