    }
  
    try {
      console.log('λ‹‰λ„¤μ„ μ¤‘λ³µ ν™•μΈ μ”μ²­:', formData.nickname); // β… μ”μ²­ μ „μ— μ½μ†” μ¶λ ¥
  
      const response = await fetch(`https://sequence.agong.store/api/users/check_username`, {
        method: 'POST', // β… λ³€κ²½λ API λ°©μ‹: POST μ”μ²­
