      </select>
      <input type="text" name="awardName" placeholder="μκ²©μ¦ λλ” μμƒλ…μ„ μ…λ ¥ν•μ„Έμ”." value={award.awardName} onChange={(e) => handleAwardChange(e, index)} />
      <button type="button" className="add-button" onClick={addAward}>+</button>
