import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Navbar from './Navbar';
import '../styles/global.css';

const Signup = () => {
  const navigate = useNavigate();

  // ✅ 단계 구분: step 1 (기본 정보) → step 2 (추가 정보)
  const [step, setStep] = useState(1);

  // ✅ 입력 데이터 상태 관리
  const [formData, setFormData] = useState({
    username: '',
    password: '',
    name: '',
    nickname: '',
    birth: '',
    gender: 'M',
    address: '',
    phone: '',
    email: '',
    introduction: '',
    portfolio: '',
    schoolName: '',
    major: '',
    grade: '',
    entranceDate: '',
    graduationDate: '',
    degree: '',
    skillCategory: [],
    desiredJob: [],
    experiences: [],
    careers: [],
    awards: [],
    profileImage: null
  });

  // ✅ 입력 필드 변경 핸들러
  const handleChange = (e) => {
    if (e.target.name === 'profileImage') {
      setFormData({ ...formData, profileImage: e.target.files[0] });
    } else {
      setFormData({ ...formData, [e.target.name]: e.target.value });
    }
  };

  // ✅ "다음" 버튼 클릭 시 step 변경
  const handleNextStep = () => {
    setStep(2);
  };

  // ✅ 회원가입 요청 처리
  const handleSubmit = async (e) => {
    e.preventDefault();

    const data = new FormData();
    data.append(
      'memberDTO',
      JSON.stringify({
        username: formData.username,
        password: formData.password,
        name: formData.name,
        nickname: formData.nickname,
        birth: formData.birth,
        gender: formData.gender,
        address: formData.address,
        phone: formData.phone,
        email: formData.email,
        introduction: formData.introduction,
        portfolio: [formData.portfolio],
        schoolName: formData.schoolName,
        major: formData.major,
        grade: formData.grade,
        entranceDate: formData.entranceDate,
        graduationDate: formData.graduationDate,
        degree: formData.degree,
        skillCategory: formData.skillCategory,
        desiredJob: formData.desiredJob,
        experiences: formData.experiences,
        careers: formData.careers,
        awards: formData.awards
      })
    );

    if (formData.profileImage) {
      data.append('authImgFile', formData.profileImage);
    }

    const response = await fetch('https://sequence-php.agong.store/api/users/join', {
      method: 'POST',
      body: data
    });

    const result = await response.json();
    alert(result.message);

    if (result.message === '회원가입 성공!') {
      navigate('/signup-success');
    }
  };

  return (
    <div>
      <Navbar />

      <section className="signup-container">
        <h1>회원가입</h1>
        <h2>{step === 1 ? '1/2' : '2/2'}</h2>

        {step === 1 ? (
          // ✅ Step 1: 기본 정보 입력
          <form className="signup-form">
            <div className="form-group">
              <label>이름</label>
              <input type="text" name="name" onChange={handleChange} required />
            </div>

            <div className="form-group">
              <label>생년월일</label>
              <input type="date" name="birth" onChange={handleChange} required />
            </div>

            <div className="form-group">
              <label>성별</label>
              <select name="gender" onChange={handleChange}>
                <option value="M">남성</option>
                <option value="F">여성</option>
              </select>
            </div>

            <div className="form-group">
              <label>휴대전화 번호</label>
              <input type="text" name="phone" onChange={handleChange} required />
            </div>

            <div className="form-group">
              <label>이메일</label>
              <input type="email" name="email" onChange={handleChange} required />
            </div>

            <div className="form-group">
              <label>주소</label>
              <input type="text" name="address" onChange={handleChange} required />
            </div>

            <button type="button" className="auth-button" onClick={handleNextStep}>
              다음
            </button>
          </form>
        ) : (
          // ✅ Step 2: 추가 정보 입력
          <form className="signup-form" onSubmit={handleSubmit}>
             <div className="profile-section">
          <h2>프로필 <span className="required">*</span></h2>
          <div className="profile-container">
            {/* 프로필 사진 업로드 */}
            <div className="profile-image-upload">
              <label htmlFor="profileImage" className="profile-upload-box">
                {formData.profileImage ? (
                  <img src={URL.createObjectURL(formData.profileImage)} alt="프로필 사진" />
                ) : (
                  <span className="upload-icon">+</span>
                )}
              </label>
              <input
                type="file"
                id="profileImage"
                name="profileImage"
                accept="image/*"
                onChange={handleChange}
                hidden
              />
            </div>

            {/* 별명 입력 */}
            <div className="nickname-section">
              <label>별명</label>
              <input
                type="text"
                name="nickname"
                placeholder="사용할 별명을 입력해주세요."
                value={formData.nickname}
                onChange={handleChange}
                maxLength={10}
              />
              <span className="char-count">{formData.nickname.length}/10</span>
              <button type="button" className="duplicate-check">중복확인</button>
            </div>
          </div>
        </div>

        {/* 🔴 학력 섹션 */}
        <div className="education-section">
          <h2>학력 <span className="required">*</span></h2>
          <div className="education-container">
            {/* 학교명 */}
            <div className="education-field">
              <label>학교명</label>
              <div className="search-input">
                <input
                  type="text"
                  name="schoolName"
                  placeholder="학교명을 입력해주세요"
                  value={formData.schoolName}
                  onChange={handleChange}
                />
                <button className="search-icon">🔍</button>
              </div>
            </div>

            {/* 전공 */}
            <div className="education-field">
              <label>전공</label>
              <input
                type="text"
                name="major"
                placeholder="전공명을 입력해주세요"
                value={formData.major}
                onChange={handleChange}
              />
            </div>

            {/* 학년 */}
            <div className="education-field">
              <label>학년</label>
              <select name="grade" value={formData.grade} onChange={handleChange}>
                <option value="">선택</option>
                <option value="1학년">1학년</option>
                <option value="2학년">2학년</option>
                <option value="3학년">3학년</option>
                <option value="4학년">4학년</option>
              </select>
            </div>

            {/* 입학 및 졸업 년도 */}
            <div className="education-field">
              <label>입학년도</label>
              <input
                type="text"
                name="entranceDate"
                placeholder="ex) 2000"
                value={formData.entranceDate}
                onChange={handleChange}
              />
            </div>

            <div className="education-field">
              <label>졸업년도</label>
              <input
                type="text"
                name="graduationDate"
                placeholder="ex) 2000"
                value={formData.graduationDate}
                onChange={handleChange}
              />
            </div>

            {/* 학적 상태 */}
            <div className="education-field">
              <label>학적 상태</label>
              <select name="degree" value={formData.degree} onChange={handleChange}>
                <option value="">선택</option>
                <option value="GRADUATED">졸업</option>
                <option value="LEAVE_OF_ABSENCE">휴학</option>
                <option value="CURRENTLY_ENROLLED">재학중</option>
              </select>
            </div>
          </div>
        </div>

            <button type="submit" className="signup-button">
              회원가입
            </button>
          </form>
        )}
      </section>
    </div>
  );
};

export default Signup;
