        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ username: formData.nickname }),
      });

      console.log('API ì‘ë‹µ ìƒíƒœ ì½”ë“œ:', response.status);
      const result = await response.json();
      console.log('ë‹‰ë„¤ì„ ì¤‘ë³µ í™•ì¸ ì‘ë‹µ:', result);

      if (result.exists) { // âœ… result.existsê°€ trueë©´ ì¤‘ë³µë¨
        setNicknameMessage('ì´ë¯¸ ì‚¬ìš© ì¤‘ì¸ ë‹‰ë„¤ì„ì…ë‹ˆë‹¤.');
        setIsNicknameAvailable(false);
      } else {
        setNicknameMessage('ì‚¬ìš© ê°€ëŠ¥í•œ ë‹‰ë„¤ì„ì…ë‹ˆë‹¤.');
        setIsNicknameAvailable(true);
      }
    } catch (error) {
      console.error('ë‹‰ë„¤ì„ ì¤‘ë³µ í™•ì¸ ì˜¤ë¥˜:', error);
      setNicknameMessage('ì„œë²„ ì˜¤ë¥˜ê°€ ë°œìƒí–ˆìŠµë‹ˆë‹¤. ë‹¤ì‹œ ì‹œë„í•´ì£¼ì„¸ìš”.');
      setIsNicknameAvailable(false);
    }
  };

  // âœ… "ë‹¤ìŒ" ë²„íŠ¼ í´ë¦­ ì‹œ step ë³€ê²½
  const handleNextStep = () => {
    setStep(2);
  };

  // âœ… íšŒì›ê°€ì… ìš”ì²­
  const handleSubmit = async (e) => {
    e.preventDefault();

    // âœ… FormData ê°ì²´ ìƒì„±
    const data = new FormData();

    // âœ… JSON ë°ì´í„° ì¶”ê°€
    const memberData = {
      username: formData.username,
      password: formData.password,
      name: formData.name,
      nickname: formData.nickname,
      birth: formData.birth,
      gender: formData.gender,
      address: formData.address,
      phone: formData.phone,
      email: formData.email,
      introduction: formData.introduction,
      portfolio: [formData.portfolio],
      schoolName: formData.schoolName,
      major: formData.major,
      grade: formData.grade,
      entranceDate: formData.entranceDate,
      graduationDate: formData.graduationDate,
      degree: formData.degree,
      skillCategory: formData.skillCategory,
      desiredJob: formData.desiredJob,
      experiences: formData.experiences,
      careers: formData.careers,
      awards: formData.awards
    };

    data.append("memberDTO", JSON.stringify(memberData));

    // âœ… í”„ë¡œí•„ ì´ë¯¸ì§€ ì¶”ê°€ (ìˆì„ ê²½ìš°)
    if (formData.profileImage) {
      data.append("authImgFile", formData.profileImage);
    }

    try {
      console.log("íšŒì›ê°€ì… ìš”ì²­ ë°ì´í„°:", memberData);

      const response = await fetch("https://sequence.agong.store/api/users/join", {
        method: "POST",
        body: data,
      });

      console.log("ì‘ë‹µ ìƒíƒœ ì½”ë“œ:", response.status);
      const result = await response.json();
      console.log("íšŒì›ê°€ì… ì‘ë‹µ ê²°ê³¼:", result);

      if (result.message === "íšŒì›ê°€ì… ì„±ê³µ!") {
        alert("ğŸ‰ íšŒì›ê°€ì…ì´ ì™„ë£Œë˜ì—ˆìŠµë‹ˆë‹¤!");
        navigate("/signup-success");
