              <div className="education-container">
                <input type="text" name="schoolName" placeholder="í•™êµëª…ì„ ì…ë ¥í•´ì£¼ì„¸ìš”" value={formData.schoolName} onChange={handleChange} />
                <input type="text" name="major" placeholder="ì „ê³µëª…ì„ ì…ë ¥í•´ì£¼ì„¸ìš”" value={formData.major} onChange={handleChange} />
                <select name="grade" value={formData.grade} onChange={handleChange}>
                  <option value="">í•™ë…„ ì„ íƒ</option>
                  <option value="1í•™ë…„">1í•™ë…„</option>
                  <option value="2í•™ë…„">2í•™ë…„</option>
                  <option value="3í•™ë…„">3í•™ë…„</option>
                  <option value="4í•™ë…„">4í•™ë…„</option>
                </select>
              </div>
            </div>
            {/* ğŸ”´ ìŠ¤í‚¬ ì„ íƒ */}
<div className="skill-section">
