      ))}
  </div>
</div>

{/* ğŸ”´ í¬ë§ ì—­í•  */}
<div className="desired-job-section">
  <h2>í¬ë§ ì—­í•  <span className="required">*</span></h2>
  <div className="desired-job-container">
    {["UX/UI Design", "BX Design", "Front-end", "Back-end", "PM"]
      .map(job => (
        <button
          type="button"
          key={job}
          className={`desired-job-button ${formData.desiredJob.includes(job) ? "selected" : ""}`}
          onClick={() => {
            setFormData(prev => ({
              ...prev,
              desiredJob: prev.desiredJob.includes(job)
                ? prev.desiredJob.filter(j => j !== job)
                : [...prev.desiredJob, job]
            }));
          }}
        >
          {job}
        </button>
      ))}
  </div>
</div>

{/* ğŸ”´ ê²½í—˜ ë° í™œë™ ì¶”ê°€ ì„¹ì…˜ */}
<div className="experience-section">
  <h2>ê²½í—˜ ë° í™œë™ì´ë ¥</h2>
  {formData.experiences.map((exp, index) => (
    <div key={index} className="experience-container">
      <select name="experienceType" value={exp.experienceType} onChange={(e) => handleExperienceChange(e, index)}>
        <option value="">ìœ í˜• ì„ íƒ</option>
        <option value="EXTERNAL_ACTIVITY">ëŒ€ì™¸í™œë™</option>
        <option value="CLUB">ë™ì•„ë¦¬</option>
      </select>
      <input type="text" name="experienceName" placeholder="í™œë™ëª…ì„ ì…ë ¥í•´ì£¼ì„¸ìš”." value={exp.experienceName} onChange={(e) => handleExperienceChange(e, index)} />
      <button type="button" className="add-button" onClick={addExperience}>+</button>
    </div>
  ))}
</div>

{/* ğŸ”´ ê²½ë ¥ ì„¹ì…˜ */}
<div className="career-section">
  <h2>ê²½ë ¥</h2>
  {formData.careers.map((career, index) => (
    <div key={index} className="career-container">
      <input type="text" name="companyName" placeholder="íšŒì‚¬ëª…ì„ ì…ë ¥í•˜ì„¸ìš”." value={career.companyName} onChange={(e) => handleCareerChange(e, index)} />
      <button type="button" className="add-button" onClick={addCareer}>+</button>
    </div>
  ))}
</div>

{/* ğŸ”´ ìê²© ë° ìˆ˜ìƒ ì„¹ì…˜ */}
<div className="award-section">
