      </select>
      <input type="text" name="awardName" placeholder="ìê²©ì¦ ë˜ëŠ” ìˆ˜ìƒëª…ì„ ì…ë ¥í•˜ì„¸ìš”." value={award.awardName} onChange={(e) => handleAwardChange(e, index)} />
      <button type="button" className="add-button" onClick={addAward}>+</button>
