import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Navbar from './Navbar'; // 네비게이션 바 추가
import '../styles/auth.css'; // 로그인 스타일

const Login = () => {
  const navigate = useNavigate(); // 페이지 이동 함수
  const [formData, setFormData] = useState({ email: '', password: '' });

  // 입력 필드 변경 핸들러
  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // 로그인 요청 처리
  const handleSubmit = async (e) => {
    e.preventDefault();

    const response = await fetch('https://sequence.agong.store/api/login', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(formData),
    });

    const result = await response.json();
    if (result.token) {
      localStorage.setItem('token', result.token);
      alert('로그인 성공!');
      navigate('/'); // 홈으로 이동
    } else {
      alert(result.message);
    }
  };

  return (
    <div>
      <Navbar /> {/* 공통 네비게이션 바 추가 */}
      <section className="auth-container">
        <h1>로그인</h1>
        <form onSubmit={handleSubmit}>
          <input type="email" name="email" placeholder="이메일" onChange={handleChange} />
          <input type="password" name="password" placeholder="비밀번호" onChange={handleChange} />
          <button type="submit">로그인</button>
        </form>
      </section>
    </div>
  );
};

export default Login;
