import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Navbar from './Navbar';
import '../styles/auth.css';

const Login = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({ email: '', password: '' });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const response = await fetch('https://sequence.agong.store/api/login', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(formData),
    });

    const result = await response.json();
    if (result.token) {
      localStorage.setItem('token', result.token);
      alert('로그인 성공!');
      navigate('/');
    } else {
      alert(result.message);
    }
  };

  return (
    <div>
      <Navbar />
      <section className="auth-container">
        <h1>로그인</h1>
        <p>회원 정보를 입력하고 로그인하세요.</p>

        <div className="recovery-form">
          <div className="form-group">
            <label htmlFor="email">이메일</label>
            <input type="email" name="email" placeholder="이메일 입력" onChange={handleChange} />
          </div>
          <div className="form-group">
            <label htmlFor="password">비밀번호</label>
            <input type="password" name="password" placeholder="비밀번호 입력" onChange={handleChange} />
          </div>
        </div>

        <button className="recovery-button" onClick={handleSubmit}>로그인</button>
      </section>
    </div>
  );
};

export default Login;
