import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Navbar from './Navbar';
import '../styles/global.css';

const Signup = () => {
  const navigate = useNavigate();
  const [step, setStep] = useState(1);
  const [formData, setFormData] = useState({
    username: '',
    password: '',
    name: '',
    nickname: '',
    birth: '',
    gender: 'M',
    address: '',
    phone: '',
    email: '',
    introduction: '',
    portfolio: '',
    schoolName: '',
    major: '',
    grade: '',
    entranceDate: '',
    graduationDate: '',
    degree: '',
    skillCategory: [],
    desiredJob: [],
    experiences: [],
    careers: [],
    awards: [],
    profileImage: null
  });

  const handleChange = (e) => {
    if (e.target.name === 'profileImage') {
      setFormData({ ...formData, profileImage: e.target.files[0] });
    } else {
      setFormData({ ...formData, [e.target.name]: e.target.value });
    }
  };

  // ‚úÖ ÎãâÎÑ§ÏûÑ Ï§ëÎ≥µÌôïÏù∏ API ÏöîÏ≤≠
  const handleNicknameCheck = async () => {
    if (!formData.nickname.trim()) {
      setNicknameMessage('ÎãâÎÑ§ÏûÑÏùÑ ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî.');
      return;
    }

    try {
      const response = await fetch(`https://sequence.agong.store/api/check-nickname?nickname=${formData.nickname}`, {
        method: 'GET',
      });

      const result = await response.json();

      if (result.available) {
        setNicknameMessage('ÏÇ¨Ïö© Í∞ÄÎä•Ìïú ÎãâÎÑ§ÏûÑÏûÖÎãàÎã§.');
        setIsNicknameAvailable(true);
      } else {
        setNicknameMessage('Ïù¥ÎØ∏ ÏÇ¨Ïö© Ï§ëÏù∏ ÎãâÎÑ§ÏûÑÏûÖÎãàÎã§.');
        setIsNicknameAvailable(false);
      }
    } catch (error) {
      setNicknameMessage('ÏÑúÎ≤Ñ Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§. Îã§Ïãú ÏãúÎèÑÌï¥Ï£ºÏÑ∏Ïöî.');
    }
  };

  const handleNextStep = () => {
    setStep(2);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const data = new FormData();
    data.append(
      'memberDTO',
      JSON.stringify(formData)
    );
    if (formData.profileImage) {
      data.append('authImgFile', formData.profileImage);
    }
    const response = await fetch('https://sequence-php.agong.store/api/users/join', {
      method: 'POST',
      body: data
    });
    const result = await response.json();
    alert(result.message);
    if (result.message === 'ÌöåÏõêÍ∞ÄÏûÖ ÏÑ±Í≥µ!') {
      navigate('/signup-success');
    }
    const addExperience = () => {
        setFormData({
          ...formData,
          experiences: [...formData.experiences, { experienceType: "", experienceName: "" }]
        });
      };
      
      const addCareer = () => {
        setFormData({
          ...formData,
          careers: [...formData.careers, { companyName: "" }]
        });
      };
      
      const addAward = () => {
        setFormData({
          ...formData,
          awards: [...formData.awards, { awardType: "", awardName: "" }]
        });
      };      
  };

  return (
    <div>
      <Navbar />
      <section className="signup-container">
        <h1>ÌöåÏõêÍ∞ÄÏûÖ</h1>
        <h2>{step === 1 ? '1/2' : '2/2'}</h2>
        {step === 1 ? (
          <form className="signup-form">
            <div className="form-group">
              <label>Ïù¥Î¶Ñ</label>
              <input type="text" name="name" onChange={handleChange} required />
            </div>
            <div className="form-group">
              <label>ÏÉùÎÖÑÏõîÏùº</label>
              <input type="date" name="birth" onChange={handleChange} required />
            </div>
            <div className="form-group">
              <label>ÏÑ±Î≥Ñ</label>
              <select name="gender" onChange={handleChange}>
                <option value="M">ÎÇ®ÏÑ±</option>
                <option value="F">Ïó¨ÏÑ±</option>
              </select>
            </div>
            <div className="form-group">
              <label>Ìú¥ÎåÄÏ†ÑÌôî Î≤àÌò∏</label>
              <input type="text" name="phone" onChange={handleChange} required />
            </div>
            <div className="form-group">
              <label>Ïù¥Î©îÏùº</label>
              <input type="email" name="email" onChange={handleChange} required />
            </div>
            <div className="form-group">
              <label>Ï£ºÏÜå</label>
              <input type="text" name="address" onChange={handleChange} required />
            </div>
            <button type="button" className="auth-button" onClick={handleNextStep}>Îã§Ïùå</button>
          </form>
        ) : (
          <form className="signup-form" onSubmit={handleSubmit}>
            <div className="profile-section">
              <h2>ÌîÑÎ°úÌïÑ *</h2>
              <div className="profile-container">
                <div className="profile-image-upload">
                  <label htmlFor="profileImage" className="profile-upload-box">
                    {formData.profileImage ? (
                      <img src={URL.createObjectURL(formData.profileImage)} alt="ÌîÑÎ°úÌïÑ ÏÇ¨ÏßÑ" />
                    ) : (
                      <span className="upload-icon">+</span>
                    )}
                  </label>
                  <input type="file" id="profileImage" name="profileImage" accept="image/*" onChange={handleChange} hidden />
                </div>
                <div className="form-group nickname-group">
            <label>Î≥ÑÎ™Ö</label>
            <div className="nickname-input-container">
              <input
                type="text"
                name="nickname"
                placeholder="ÏÇ¨Ïö©Ìï† Î≥ÑÎ™ÖÏùÑ ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî."
                value={formData.nickname}
                onChange={handleChange}
                maxLength={10}
              />
              <button type="button" className="duplicate-check" onClick={handleNicknameCheck}>
                Ï§ëÎ≥µÌôïÏù∏
              </button>
            </div>
            <p className={`nickname-message ${isNicknameAvailable ? 'success' : 'error'}`}>{nicknameMessage}</p>
          </div>
          
              </div>
            </div>
            <div className="education-section">
              <h2>ÌïôÎ†• *</h2>
              <div className="education-container">
                <input type="text" name="schoolName" placeholder="ÌïôÍµêÎ™ÖÏùÑ ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî" value={formData.schoolName} onChange={handleChange} />
                <input type="text" name="major" placeholder="Ï†ÑÍ≥µÎ™ÖÏùÑ ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî" value={formData.major} onChange={handleChange} />
                <select name="grade" value={formData.grade} onChange={handleChange}>
                  <option value="">ÌïôÎÖÑ ÏÑ†ÌÉù</option>
                  <option value="1ÌïôÎÖÑ">1ÌïôÎÖÑ</option>
                  <option value="2ÌïôÎÖÑ">2ÌïôÎÖÑ</option>
                  <option value="3ÌïôÎÖÑ">3ÌïôÎÖÑ</option>
                  <option value="4ÌïôÎÖÑ">4ÌïôÎÖÑ</option>
                </select>
              </div>
            </div>
            {/* üî¥ Ïä§ÌÇ¨ ÏÑ†ÌÉù */}
<div className="skill-section">
  <h2>Ïä§ÌÇ¨ <span className="required">*</span></h2>
  <div className="skill-container">
    {["Adobe Illustration", "Adobe Photoshop", "Adobe Indesign", "JavaScript", "TypeScript", "Figma",
      "Adobe Premiere Pro", "Adobe XD", "Adobe After Effect", "Adobe Lightroom", "Cinema 4D", "ProtoPie", "Blender"]
      .map(skill => (
        <button
          type="button"
          key={skill}
          className={`skill-button ${formData.skillCategory.includes(skill) ? "selected" : ""}`}
          onClick={() => {
            setFormData(prev => ({
              ...prev,
              skillCategory: prev.skillCategory.includes(skill)
                ? prev.skillCategory.filter(s => s !== skill)
                : [...prev.skillCategory, skill]
            }));
          }}
        >
          {skill}
        </button>
      ))}
  </div>
</div>

{/* üî¥ Ìù¨Îßù Ïó≠Ìï† */}
<div className="desired-job-section">
  <h2>Ìù¨Îßù Ïó≠Ìï† <span className="required">*</span></h2>
  <div className="desired-job-container">
    {["UX/UI Design", "BX Design", "Front-end", "Back-end", "PM"]
      .map(job => (
        <button
          type="button"
          key={job}
          className={`desired-job-button ${formData.desiredJob.includes(job) ? "selected" : ""}`}
          onClick={() => {
            setFormData(prev => ({
              ...prev,
              desiredJob: prev.desiredJob.includes(job)
                ? prev.desiredJob.filter(j => j !== job)
                : [...prev.desiredJob, job]
            }));
          }}
        >
          {job}
        </button>
      ))}
  </div>
</div>

{/* üî¥ Í≤ΩÌóò Î∞è ÌôúÎèô Ï∂îÍ∞Ä ÏÑπÏÖò */}
<div className="experience-section">
  <h2>Í≤ΩÌóò Î∞è ÌôúÎèôÏù¥Î†•</h2>
  {formData.experiences.map((exp, index) => (
    <div key={index} className="experience-container">
      <select name="experienceType" value={exp.experienceType} onChange={(e) => handleExperienceChange(e, index)}>
        <option value="">Ïú†Ìòï ÏÑ†ÌÉù</option>
        <option value="EXTERNAL_ACTIVITY">ÎåÄÏô∏ÌôúÎèô</option>
        <option value="CLUB">ÎèôÏïÑÎ¶¨</option>
      </select>
      <input type="text" name="experienceName" placeholder="ÌôúÎèôÎ™ÖÏùÑ ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî." value={exp.experienceName} onChange={(e) => handleExperienceChange(e, index)} />
      <button type="button" className="add-button" onClick={addExperience}>+</button>
    </div>
  ))}
</div>

{/* üî¥ Í≤ΩÎ†• ÏÑπÏÖò */}
<div className="career-section">
  <h2>Í≤ΩÎ†•</h2>
  {formData.careers.map((career, index) => (
    <div key={index} className="career-container">
      <input type="text" name="companyName" placeholder="ÌöåÏÇ¨Î™ÖÏùÑ ÏûÖÎ†•ÌïòÏÑ∏Ïöî." value={career.companyName} onChange={(e) => handleCareerChange(e, index)} />
      <button type="button" className="add-button" onClick={addCareer}>+</button>
    </div>
  ))}
</div>

{/* üî¥ ÏûêÍ≤© Î∞è ÏàòÏÉÅ ÏÑπÏÖò */}
<div className="award-section">
  <h2>ÏûêÍ≤© Î∞è ÏàòÏÉÅ</h2>
  {formData.awards.map((award, index) => (
    <div key={index} className="award-container">
      <select name="awardType" value={award.awardType} onChange={(e) => handleAwardChange(e, index)}>
        <option value="">Ïú†Ìòï ÏÑ†ÌÉù</option>
        <option value="CERTIFICATE">ÏûêÍ≤©Ï¶ù</option>
        <option value="AWARD">ÏàòÏÉÅ</option>
      </select>
      <input type="text" name="awardName" placeholder="ÏûêÍ≤©Ï¶ù ÎòêÎäî ÏàòÏÉÅÎ™ÖÏùÑ ÏûÖÎ†•ÌïòÏÑ∏Ïöî." value={award.awardName} onChange={(e) => handleAwardChange(e, index)} />
      <button type="button" className="add-button" onClick={addAward}>+</button>
    </div>
  ))}
</div>

{/* üî¥ Ìè¨Ìä∏Ìè¥Î¶¨Ïò§ ÏÑπÏÖò */}
<div className="portfolio-section">
  <h2>Ìè¨Ìä∏Ìè¥Î¶¨Ïò§</h2>
  <input type="file" id="portfolioFile" hidden />
  <label htmlFor="portfolioFile">Ìè¨Ìä∏Ìè¥Î¶¨Ïò§ ÏóÖÎ°úÎìú ÎòêÎäî URL ÏûÖÎ†•</label>
</div>

{/* üî¥ ÏûêÍ∏∞ÏÜåÍ∞ú */}
<div className="introduction-section">
  <h2>ÏûêÍ∏∞ÏÜåÍ∞ú</h2>
  <textarea
    name="introduction"
    placeholder="ÌåÄÏõêÏùÑ Î™®ÏßëÌï† Îïå Ï∞∏Í≥†Ìï† ÏûêÍ∏∞ÏÜåÍ∞úÎ•º 500Ïûê Ïù¥ÎÇ¥Î°ú Ï†ÅÏñ¥Ï£ºÏÑ∏Ïöî!"
    maxLength={500}
    value={formData.introduction}
    onChange={handleChange}
  />
  <span className="char-count">{formData.introduction.length}/500</span>
</div>


            
            <button type="submit" className="signup-button">ÌöåÏõêÍ∞ÄÏûÖ</button>
          </form>
        )}
      </section>
    </div>
  );
};

export default Signup;
