import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Navbar from './Navbar';
import '../styles/global.css';

const Signup = () => {
  const navigate = useNavigate();
  const [step, setStep] = useState(1);
  const [formData, setFormData] = useState({
    username: '',
    password: '',
    name: '',
    nickname: '',
    birth: '',
    gender: 'M',
    address: '',
    phone: '',
    email: '',
    introduction: '',
    portfolio: '',
    schoolName: '',
    major: '',
    grade: '',
    entranceDate: '',
    graduationDate: '',
    degree: '',
    skillCategory: [],
    desiredJob: [],
    experiences: [],
    careers: [],
    awards: [],
    profileImage: null
  });

  const handleChange = (e) => {
    if (e.target.name === 'profileImage') {
      setFormData({ ...formData, profileImage: e.target.files[0] });
    } else {
      setFormData({ ...formData, [e.target.name]: e.target.value });
    }
  };

  const handleNextStep = () => {
    setStep(2);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const data = new FormData();
    data.append(
      'memberDTO',
      JSON.stringify(formData)
    );
    if (formData.profileImage) {
      data.append('authImgFile', formData.profileImage);
    }
    const response = await fetch('https://sequence-php.agong.store/api/users/join', {
      method: 'POST',
      body: data
    });
    const result = await response.json();
    alert(result.message);
    if (result.message === '회원가입 성공!') {
      navigate('/signup-success');
    }
  };

  return (
    <div>
      <Navbar />
      <section className="signup-container">
        <h1>회원가입</h1>
        <h2>{step === 1 ? '1/2' : '2/2'}</h2>
        {step === 1 ? (
          <form className="signup-form">
            <div className="form-group">
              <label>이름</label>
              <input type="text" name="name" onChange={handleChange} required />
            </div>
            <div className="form-group">
              <label>생년월일</label>
              <input type="date" name="birth" onChange={handleChange} required />
            </div>
            <div className="form-group">
              <label>성별</label>
              <select name="gender" onChange={handleChange}>
                <option value="M">남성</option>
                <option value="F">여성</option>
              </select>
            </div>
            <div className="form-group">
              <label>휴대전화 번호</label>
              <input type="text" name="phone" onChange={handleChange} required />
            </div>
            <div className="form-group">
              <label>이메일</label>
              <input type="email" name="email" onChange={handleChange} required />
            </div>
            <div className="form-group">
              <label>주소</label>
              <input type="text" name="address" onChange={handleChange} required />
            </div>
            <button type="button" className="auth-button" onClick={handleNextStep}>다음</button>
          </form>
        ) : (
          <form className="signup-form" onSubmit={handleSubmit}>
            <div className="profile-section">
              <h2>프로필 *</h2>
              <div className="profile-container">
                <div className="profile-image-upload">
                  <label htmlFor="profileImage" className="profile-upload-box">
                    {formData.profileImage ? (
                      <img src={URL.createObjectURL(formData.profileImage)} alt="프로필 사진" />
                    ) : (
                      <span className="upload-icon">+</span>
                    )}
                  </label>
                  <input type="file" id="profileImage" name="profileImage" accept="image/*" onChange={handleChange} hidden />
                </div>
                <div className="nickname-section">
                  <label>별명</label>
                  <input type="text" name="nickname" placeholder="사용할 별명을 입력해주세요." value={formData.nickname} onChange={handleChange} maxLength={10} />
                  <button type="button" className="duplicate-check">중복확인</button>
                </div>
              </div>
            </div>
            <div className="education-section">
              <h2>학력 *</h2>
              <div className="education-container">
                <input type="text" name="schoolName" placeholder="학교명을 입력해주세요" value={formData.schoolName} onChange={handleChange} />
                <input type="text" name="major" placeholder="전공명을 입력해주세요" value={formData.major} onChange={handleChange} />
                <select name="grade" value={formData.grade} onChange={handleChange}>
                  <option value="">학년 선택</option>
                  <option value="1학년">1학년</option>
                  <option value="2학년">2학년</option>
                  <option value="3학년">3학년</option>
                  <option value="4학년">4학년</option>
                </select>
              </div>
            </div>
            
            <button type="submit" className="signup-button">회원가입</button>
          </form>
        )}
      </section>
    </div>
  );
};

export default Signup;
